<?phpdefined('BASEPATH') OR exit('No direct script access allowed');class Api_model extends CI_Model {    var $table = 'booksapi';    public function __construct() {        parent::__construct();        $this->load->database();    }    public function get_all() {        $this->db->from($this->table);        $query = $this->db->get();        return $query->result();    }    public function get_all_by_dsID($id) {        $this->db->from($this->table);        $this->db->where('dsID', $id);        $query = $this->db->get();        return $query->result();    }//   public function get_all_cursos() {////        $this->db->select('c.cursoID,c.curso,c.gradoID,c.seccion,c.maestroID,c.orden,c.anio,g.grado, m.maestro');    ////        $this->db->from('curso c');////        $this->db->join('grado g', 'c.gradoID = g.gradoID', 'left');////        $this->db->join('maestro m', 'c.maestroID = m.maestroID', 'left');////        $query = $this->db->get();////        return $query->result();////    }    public function get_http_by_id($id) {        $sql_select = 'SELECT http FROM books_crud.booksapi where methodID = '.$id.';';        $query = $this->db->query($sql_select);        return $query->row()->http;    }    public function get_by_id($id) {        $this->db->from($this->table);        $this->db->where('methodID', $id);        $query = $this->db->get();        return $query->row();    }    public function api_add($data) {        $this->db->insert($this->table, $data);        return $this->db->insert_id();    }    public function api_update($where, $data) {        $this->db->update($this->table, $data, $where);        return $this->db->affected_rows();    }    public function delete_by_id($id) {        $this->db->where('methodID', $id);        $this->db->delete($this->table);    }}